import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Post } from '../models/post.model';

@Injectable({
  providedIn: 'root'
})
export class PostService {
apiurl='http://localhost:3000/postApi';
posts:any;
  constructor(private http: HttpClient) {
    this.chargerListPost().subscribe((listApp) => {
      console.log(listApp);
    });
  }

  chargerListPost() : Observable<Post[]>{
    return this.http.get<Post[]>('http://localhost:3000/postApi');
  }

  getPost() : Observable<Post[]>{
    return this.http.get<Post[]>(this.apiurl);
  }
 addpost(a:Post) : Observable<Post[]>{
    return this.http.post<Post[]>(this.apiurl,a);
  }
  getpostbyId(id:string) : Observable<Post[]>{
    return this.http.get<Post[]>(this.apiurl+id);
  }
  updatepost(a:Post): Observable<any>{
return this.http.put<any>(this.apiurl+a.id,a);
  }
  deletepost(id:string): Observable<any>{
return this.http.delete<any>(this.apiurl+id);
  }
}
